import React, { useState } from 'react';
import '../App.css';
import Header from './Header';
import AddressForm from './AddressForm';

function ShirtCarioca(props) {
    const [shirtSku, setShirtSku] = useState('sku_Fhr4OckKneZqEE');
    const [shirtSize, setShirtSize] = useState('small');
    const [purchaseClicked, setPurchaseClicked] = useState(false);

    //get the right sku for checkout button
    function shirtSelection(e) {
        //change the URL of the button based on the shirt size selection
        if (e.currentTarget.value === 'small') {
            setShirtSku('sku_Fhr4OckKneZqEE');
            setShirtSize('Small');
        } else if (e.currentTarget.value === 'medium') {
            setShirtSku('sku_FihP9XufHxebxv');
            setShirtSize('Medium');
        } else if (e.currentTarget.value === 'large') {
            setShirtSku('sku_Fhpj28aZk8DWnG');
            setShirtSize('Large');
        }
    }

    //STRIPE CHECKOUT
    var stripe = window.Stripe('pk_test_tFe2ikD0cZ9k1LB6zKbgr12W00GN6tkMxR');

    //when you click the pay button
    function buyNow(e) {
        e.preventDefault();
        console.log('button clicked');

        // When the customer clicks on the button, redirect
        // them to Checkout.
        stripe
            .redirectToCheckout({
                items: [{ sku: shirtSku, quantity: 1 }],

                // Do not rely on the redirect to the successUrl for fulfilling
                // purchases, customers may not always reach the success_url after
                // a successful payment.
                // Instead use one of the strategies described in
                // https://stripe.com/docs/payments/checkout/fulfillment
                successUrl: window.location.protocol + '//lekikitrend.com/success',
                cancelUrl: window.location.protocol + '//lekikitrend.com/canceled'
                // customerEmail: email
            })
            .then(function(result) {
                if (result.error) {
                    // If `redirectToCheckout` fails due to a browser or network
                    // error, display the localized error message to your customer.
                    var displayError = document.getElementById('error-message');
                    displayError.textContent = result.error.message;
                }
            });
    }

    function purchaseClick() {
        setPurchaseClicked(true);
    }
    //if back button is clicked
    function backClick() {
        this.setPurchaseClicked(false);
    }

    if (purchaseClicked) {
        return (
            <div className="address-container">
                <Header />
                <AddressForm shirtName="Carioca" size={shirtSize} backClick={backClick()} />
            </div>
        );
    }

    return (
        <div className="shirt-page">
            <Header />
            <div className="shirt-layout">
                {/* Shirt Image */}
                <img
                    src="/static/media/shirt.47aab29a.png"
                    alt="Gray shirt with Christ statue looking over pão de açucar"
                />
                {/* Shirt Text */}
                <div className="shirt-text">
                    <h2>Carioca (t-shirt)</h2>
                    <h3>$25</h3>
                    <p className="small-text margin-bottom-24">Free Shipping</p>
                    {/* Shirt Size Picker */}
                    <form className="shirt-size-selection margin-bottom-32" onSubmit={buyNow}>
                        <label>Select Shirt Size</label>
                        <div className="form-row">
                            <label>
                                <input
                                    type="radio"
                                    name="shirtsize"
                                    value="small"
                                    onChange={shirtSelection}
                                    defaultChecked
                                />
                                Small
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    name="shirtsize"
                                    value="medium"
                                    onChange={shirtSelection}
                                />
                                Medium
                            </label>
                            <label>
                                <input
                                    type="radio"
                                    name="shirtsize"
                                    value="large"
                                    onChange={shirtSelection}
                                />
                                Large
                            </label>
                        </div>
                        <button
                            type="button"
                            className="primary"
                            role="link"
                            onClick={purchaseClick}
                        >
                            Purchase
                        </button>
                        <div id="error-message"></div>
                    </form>
                </div>
            </div>
        </div>
    );
}

export default ShirtCarioca;
