import React, { useState } from 'react';
import * as emailjs from 'emailjs-com';

function AddressForm(props) {
    const [email, setEmail] = useState('');
    const [name, setName] = useState('');
    const [address, setAddress] = useState('');
    const [city, setCity] = useState('');
    const [st, setSt] = useState('');
    const [zip, setZip] = useState('');

    //if back button is clicked
    function backClick() {
        this.setPurchaseClicked(false);
    }

    //validate the form funciton

    function submitBtn(e) {
        e.preventDefault();

        //validate the forms

        //gather props from inputs
        let templateParams = {
            name: name,
            email: email,
            address: address,
            city: city,
            state: st,
            zip: zip
        };
        // Send email to me with information from user
        emailjs
            .send('gmail', 'lekiki_trend_t_shirt', templateParams, 'user_ZNRCkUWp1sx3jKPQ1AAV1')
            .then(
                function(response) {
                    console.log('SUCCESS!', response.status, response.text);
                },
                function(error) {
                    console.log('FAILED...', error);
                }
            );
    }

    return (
        <div>
            <button onClick={backClick}>Back</button>
            <h3 className="margin-bottom-24">Please enter your email & shipping info</h3>
            <form id="shipping-info" onSubmit={submitBtn}>
                <div className="form-row">
                    <label>Email</label>
                    <input
                        name="email"
                        type="email"
                        onChange={e => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>Name on Package</label>
                    <input
                        name="name"
                        type="text"
                        onChange={e => setName(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>Address</label>
                    <input
                        name="address"
                        type="text"
                        onChange={e => setAddress(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>City</label>
                    <input
                        name="city"
                        type="text"
                        onChange={e => setCity(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>State</label>
                    <input
                        name="state"
                        type="text"
                        onChange={e => setSt(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>Zip</label>
                    <input
                        className="margin-bottom-24"
                        name="zip"
                        type="number"
                        onChange={e => setZip(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Finalize Payment</button>
                {/* <button className="primary" role="link" onClick={buyNow}>
                    Purchase
                </button> */}
                <div className="note">
                    <p>Note: If you are having difficulty, please contact lekikitrend@gmail.com</p>
                </div>
            </form>
        </div>
    );
}

export default AddressForm;
