import React, { useState } from 'react';
import * as emailjs from 'emailjs-com';

function AddressForm(props) {
    const [email, setEmail] = setState('');
    const [address, setAddress] = setState('');
    const [city, setCity] = setState('');
    const [st, setSt] = setState('');
    const [zip, setZip] = setState('');

    function submitBtn(e) {
        e.preventDefault();
        const message =
            'Email:' +
            email +
            ' Address:' +
            address +
            ' City:' +
            city +
            ' State:' +
            st +
            ' Zip:' +
            zip;

        let templateParams = {
            from_name: 'ashby.kyle@gmail.com',
            to_name: '<YOUR_EMAIL_ID>',
            subject: 'shirt order',
            message_html: message
        };
        emailjs.send(
            'gmail',
            'lekiki_trend_t_shirt',
            templateParams,
            ' user_ZNRCkUWp1sx3jKPQ1AAV1'
        );
    }

    return (
        <div>
            <label>Shirt: {props.shirtName}</label>
            <label className="margin-bottom-16">Size: {props.size}</label>
            <h3 class="margin-bottom-24">Please enter your email & shipping info</h3>
            <form id="shipping-info" onSubmit={submitBtn}>
                <div className="form-row">
                    <label>Email</label>
                    <input
                        name="email"
                        type="text"
                        onChange={e => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>Address</label>
                    <input
                        name="address"
                        type="text"
                        onChange={e => setAddress(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>City</label>
                    <input
                        name="city"
                        type="text"
                        onChange={e => setCity(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>State</label>
                    <input
                        name="state"
                        type="text"
                        onChange={e => setSt(e.target.value)}
                        required
                    />
                </div>
                <div className="form-row">
                    <label>Zip</label>
                    <input
                        className="margin-bottom-24"
                        name="zip"
                        type="number"
                        onChange={e => setZip(e.target.value)}
                        required
                    />
                </div>
                <button type="submit">Finalize Payment</button>
                {/* <button className="primary" role="link" onClick={buyNow}>
                    Purchase
                </button> */}
            </form>
        </div>
    );
}

export default AddressForm;
